var expect = require("chai").expect;

module.exports = function (helpers) {
    var comment = helpers.vdom.createComment("This is a comment");
    var commentClone = comment.ba_();
    expect(commentClone).to.not.equal(comment);
    expect(comment.ax_).to.equal("This is a comment");
    expect(commentClone.ax_).to.equal("This is a comment");
    return comment;
};