var expect = require("chai").expect;

module.exports = function (helpers) {
    var SVG_FLAGS = 1; // SVG

    var svg = helpers.vdom.createElement("svg", null /* attrs */
    , null /* key */
    , null /* component */
    , null /* child count */
    , SVG_FLAGS);

    var docFragment = helpers.vdom.createDocumentFragment();
    svg.aY_(docFragment);

    expect(svg.bn_).to.equal("http://www.w3.org/2000/svg");

    var docFragmentClone = docFragment.ba_();
    expect(docFragmentClone.as_).to.equal(null);
    expect(docFragmentClone.bi_ == null).to.equal(true);

    return svg;
};